mapper.select:
SELECT *
FROM CommunityLike
WHERE
	member_id = #{member_id}
	AND community_like_status = #{community_like_status}
	AND community_num = #{community_num}

mapper.delete:
DELETE FROM CommunityLike
WHERE
	member_id = #{member_id}
	AND community_like_status = #{community_like_status}
	AND community_num = #{community_num}

