mapper.update:
UPDATE Member
SET
	member_level = #{member_level}
	, member_exp = #{member_exp}
WHERE member_id = #{member_id}

mapper.insert:
INSERT INTO Review
VALUES(
	null
	, #{member_id}
	, #{target_member_id}
	, #{order_id}
	, #{review_status}
	, IFNULL(#{review_check1}, 'off')
	, IFNULL(#{review_check2}, 'off')
	, IFNULL(#{review_check3}, 'off')
	, IFNULL(#{review_check4}, 'off')
)
