mapper.select:
SELECT *
FROM PayHistory
WHERE pay_id = #{pay_id}

	AND pay_history_type = #{pay_history_type}

ORDER BY pay_history_date DESC
LIMIT #{startRow}, #{listLimit}

SELECT COUNT(*)
FROM PayHistory
WHERE pay_id = #{pay_id}

	AND pay_history_type = #{pay_history_type}

mapper.update:
