mapper.select:
SELECT member_id
FROM Member
WHERE member_nickname = #{q}

SELECT search_count
FROM Search
WHERE search_content = #{keyword}
UNION ALL
SELECT 0 AS search_count
WHERE NOT EXISTS (
    SELECT search_count
    FROM Search
    WHERE search_content = #{keyword}
)

mapper.update:
UPDATE Search
SET search_count = search_count + 1
WHERE search_content = #{keyword}

UPDATE Search
SET delete_status = 1
WHERE search_content = #{content}
